pragma solidity >0.5.0 < 0.8.0;

library cryptosuite{

    function splitSignature(bytes memory sig) internal pure returns(uint8 v, bytes32 r, bytes32 s)
    {
        require(sig.length==65);

        assembly{
            //first 32bytes
            r := mload(add(sig, 32))

            //next 32bytes
            s := mload(add(sig,64))

            //last 32bytes
            v := byte(0 , mload(add(sig,96)))

        }

        return (v,r,s);

    }

    function recoverySigner(bytes32 message, bytes memory sig) internal pure returns(address)
    {
        (uint8 v, bytes32 r, bytes32 s) = splitSignature(sig);
        return ecrecover(message, v,r,s);
    }










}


contract Coldchain{

    enum Mode{ ISSUER, PROVER, VERIFIER }

    struct Entity{
        address id;
        Mode mode;
        uint[] certificateIds;

    }

    enum certificateStatus{ MANUFACTURED, DELIVERING_INTERNATIONAL, STORED, DELIVERING_LOCAL, DELIVERED}

    struct certificate {
        uint id;
        Entity issuer;
        
    }











}
